import { Component, OnInit, ElementRef } from '@angular/core';
import {AngularFirestore,  } from '@angular/fire/compat/firestore';
import {v4 as uuidv4} from 'uuid';
import { Observable } from 'rxjs';


import { AddPostService } from '../add-post';




@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.css'],

})
export class GameComponent implements OnInit  {




    title = '';
    content = [];
    ErrorInput: String ="";
     posts: Observable<any[]> | undefined;






  bodyText: string ="" ;

    constructor(private modalService: AddPostService, private db:AngularFirestore, public myElement: ElementRef ) { 
      this.posts = db.collection('posts').valueChanges();

    }




    ngOnInit() {
        this.bodyText = 'This text can be updated in modal 1';



    }


    scrollToTop(){
      window.scroll(0,0);
    }



    openModal(id: string) {
        this.modalService.open(id);
        this.title=""
        this.content=[]
    }

    closeModal(id: string) {
        this.modalService.close(id);
    }

    async addPost(){

      if(this.title !='' && this.content !=[]){
        this.ErrorInput=""
        var id : string = uuidv4();


        this.db.collection('posts').doc(id).set({
         title : this.title,
         content:this.content,

         Id :  id
       });
      }
      else{
        console.log("Error Input")
        this.ErrorInput = "Please Input title and content"

      }   
      }

      async deletePost(pID: any){

        this.db.collection('posts').doc(pID).delete();
      }

      async getIdandpushtoedit(pID:any , postTitle: any, postContent:any ){
          localStorage.setItem('postId', pID);
          localStorage.setItem('postTitle', postTitle);
          localStorage.setItem('postContent', postContent );
      }


}
<div class="first_block" >
 <button class="button_first_block" >Помощь? </button>
 <button class="button_first_block" (click)="openModal('custom-modal-1')"> Добавить пост </button>
 <button class="button_first_block"> Нашли ошибку ? </button>
</div>
<div class="second_block">

    <p class="p1_text">IT_INFO.UA</p>
    <table class="tr_first">
        <tr>
            <td><button class="button_second_block" routerLink="/game " >Игры</button></td>
            <td><button class="button_second_block" routerLink="/technique">Техника </button> </td>
            <td><button class="button_second_block">Обзоры</button></td>
            <td><button class="button_second_block">Электроника</button></td>
        </tr>
    </table>
    <p class="p_block" > Про Игры </p>


</div>


<div style= "margin-left: 450px; padding: 5px; width: 550px ; position: absolute;" >

        <div  style="padding: 12px; border-style:solid; border-radius: 5px; margin-top: 12px;"  *ngFor="let post of posts | async">
        <button class="delete_button"  type ="submit" (click)="deletePost(post.Id)" >Delete</button>
        <button  routerLink="/edit"  (click)=" getIdandpushtoedit(post.Id, post.title , post.content)" class="edit_button" >   Edit </button>


        <table >
            <tr style=" font-size: 20px;" ><b>{{ post.title }}</b></tr>
            <td style="width: 550px;"  >{{ post.content }}</td>
        </table>


    </div>
  </div>


<jw-modal id="custom-modal-1">
    <form>
        <p style=" text-align: center; font-size: 20px; color: white;"> Title </p>
    <div><input class="input" name= "title" id ="title" type="text" [(ngModel)] = "title"   /></div>
        <p style=" text-align: center; font-size: 20px; color: white; padding: 10px; "> Content </p>
    <div><textarea class=input_post  name="content" id="content" type="textarea" [(ngModel)] = "content"   ></textarea></div> 
    <button class="button_popup" (click)="closeModal('custom-modal-1');"> Закрыть</button>
    <button class="button_popup" type ="submit" (click)="addPost()"   > Сохранить </button>

    </form>
    <div>   <p class = "message"> {{ErrorInput}}</p>  </div> 

</jw-modal>



<button (click)="scrollToTop()" class="top_button"  >
    <img  src="assets\image\to_top.png">
 </button>
<div class="footer">
    <p  class="p1_text">IT_INFO.UA</p>

</div>
